mapper.insert:
insert into lms_member(
    memberid
    , memberpw
    , membername
    , birthday
    , phone
    , gender
    , email
    , address
    )
values(
    #{memberid}
    , #{memberpw}
    , #{membername}
    , #{birthday}
    , #{phone}
    , #{gender}
    , #{email}
    , #{address}
    )

mapper.select:
select membername
, birthday , phone , email , address , CASE WHEN gender = 'male' THEN '남성' ELSE '여성' END AS gender
from lms_member where memberid = #{memberid}

mapper.update:
update lms_member
set
membername = #{membername}

    , memberpw = #{memberpw}


    , memberpw = memberpw

, gender = #{gender}
, birthday = #{birthday}
, email = #{email}
, phone = #{phone}
, address = #{address}

, memberphoto=#{memberphoto}


where
memberid = #{memberid}

update lms_member
set
memberpw = memberpw

    ,memberphoto=#{memberphoto}

where memberid=#{memberid}
