mapper.insert:

    SELECT lms_question_seq.CURRVAL FROM DUAL

insert into lms_question (
testid, qid, papernum, contents, points, type

    , originalfile, savedfile

) values (
#{testid}, lms_question_seq.nextval, #{papernum}, #{contents}, #{points}, #{type}

    , #{originalfile}, #{savedfile}

)

INSERT ALL

    INTO lms_q_options (optionid, qid, value

        , content


        , checked

    )
    VALUES (#{options.optionid}, #{options.qid}, #{options.value}

        , #{options.content}


        , #{options.checked}

    )

SELECT * FROM dual

mapper.update:
UPDATE lms_question
SET
contents = #{contents},
points = #{points}

    , originalfile = #{originalfile}
    , savedfile = #{savedfile}

WHERE qid = #{qid}

UPDATE lms_q_options
SET
checked = #{checked}

    ,value = #{value}


    , content = #{content}

WHERE optionid = #{optionid}

mapper.insert:
INSERT INTO lms_test_answer (
    answernum
    , asnum
    , qid
    , answer

    , originalfile
    , savedfile

)
VALUES (
    lms_test_answer_seq.nextval
    , #{asnum}
    , #{qid}
    , #{answer}

    , #{originalfile}
    , #{savedfile}

)

mapper.select:
select count(*)
from lms_q_options
where qid = #{qid}

mapper.update:
UPDATE lms_test_answer
SET
    points = #{points}
WHERE
    asnum = #{asnum}
    AND qid = #{qid}
